import { Component, OnInit } from '@angular/core';
import { MediaService } from '../shared/media.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-home',
  standalone: true,
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  imports: [CommonModule, FormsModule]
})
export class HomeComponent implements OnInit {
  mediaList: any[] = [];
  showModal = false;

  newMedium: any = {
    title: '',
    category: '',
    status: '',
    rating: null,
    comment: ''
  };

  customFields: { key: string, value: string }[] = [];

  constructor(private mediaService: MediaService) {}

  ngOnInit(): void {
    this.loadMedia();
  }

  loadMedia(): void {
    this.mediaService.getAllMedia().subscribe({
      next: (data) => this.mediaList = data,
      error: (err) => console.error('Fehler beim Laden der Medien:', err)
    });
  }

  getKeys(obj: any): string[] {
    return Object.keys(obj);
  }

  getCategoryClass(category: string | undefined): string {
    return category?.trim().toLowerCase().replace(/\s+/g, '-') || '';
  }

  openModal(): void {
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.resetForm();
  }

  addCustomField(): void {
    this.customFields.push({ key: '', value: '' });
  }

  toggleStatus(status: string): void {
    this.newMedium.status = status;
    if (status !== 'erledigt') {
      this.newMedium.rating = null;
      this.newMedium.comment = '';
    }
  }

  onSubmit(): void {
    const fullMedium: any = {
      title: this.newMedium.title,
      category: this.newMedium.category,
      status: this.newMedium.status
    };

    if (this.newMedium.status === 'erledigt') {
      fullMedium.rating = this.newMedium.rating;
      fullMedium.comment = this.newMedium.comment;
    }

    const dynamicFields: any = {};
    this.customFields.forEach(field => {
      if (field.key && field.value) {
        dynamicFields[field.key] = field.value;
      }
    });

    fullMedium.dynamicFields = dynamicFields;

    this.mediaService.createMedia(fullMedium).subscribe({
      next: (savedMedium) => {
        this.mediaList.push(savedMedium);
        this.closeModal();
        this.loadMedia();
      },
      error: (err) => console.error('Fehler beim Speichern des Mediums:', err)
    });
  }

  resetForm(): void {
    this.newMedium = {
      title: '',
      category: '',
      status: '',
      rating: null,
      comment: ''
    };
    this.customFields = [];
  }

  editMedium(medium: any): void {
    console.log("Bearbeiten:", medium);
  }

  deleteMedium(id: string): void {
    console.log("Löschen ID:", id);
  }
}


<main class="main-container">
    <div class="content-wrapper">
  
      <!-- Kategorien -->
      <ul class="category-list">
        <li class="category-item">Alle</li>
        <li class="category-item buch">Bücher</li>
        <li class="category-item film">Filme</li>
        <li class="category-item ps-spiel">PS-Spiele</li>
        <li class="category-item gesellschaftsspiel">Gesellschaftsspiele</li>
      </ul>
  
      <div class="media-card" *ngFor="let medium of mediaList" [ngClass]="getCategoryClass(medium.category)">
  
        <div class="media-card-actions">
          <button class="expand-button" (click)="toggleCard(medium._id)">
            <i class="bi" [ngClass]="isExpanded(medium._id) ? 'bi-dash-square' : 'bi-plus-square'"></i>
          </button>
          <button class="edit-button" (click)="editMedium(medium)">
            <i class="bi bi-pencil-square"></i>
          </button>
          <button class="delete-button" (click)="deleteMedium(medium._id)">
            <i class="bi bi-trash"></i>
          </button>
        </div>
      
        <h6 class="media-card-title">{{ medium.title }}</h6>
        <p class="media-card-type">{{ medium.category }}</p>
      
        <!-- Nur bei expandierter Karte anzeigen -->
        <div *ngIf="isExpanded(medium._id)" class="media-card-details">
          <ng-container *ngFor="let key of getKeys(medium)">
            <p *ngIf="!['title', 'category', '_id', '__v', 'createdAt', 'updatedAt'].includes(key)">
              <strong>{{ key | titlecase }}:</strong> {{ medium[key] }}
            </p>
          </ng-container>
      
          <!-- Optional: dynamicFields separat -->
          <ng-container *ngIf="medium.dynamicFields">
            <p *ngFor="let key of getKeys(medium.dynamicFields)">
              <strong>{{ key | titlecase }}:</strong> {{ medium.dynamicFields[key] }}
            </p>
          </ng-container>
        </div>
      </div>
      


    <!-- MODAL: Neues Medium hinzufügen -->
<div class="modal-backdrop" *ngIf="showModal"></div>

<div class="modal" *ngIf="showModal">
  <div class="add-medium-modal">
    <h2>Neues Medium hinzufügen</h2>

    <form (ngSubmit)="onSubmit()" class="modal-form">
      <!-- Titel -->
      <div class="form-group">
        <label for="title">Titel:</label>
        <input type="text" id="title" [(ngModel)]="newMedium.title" name="title" required />
      </div>

      <!-- Kategorie -->
      <div class="form-group">
        <label for="category">Kategorie:</label>
        <select id="category" [(ngModel)]="newMedium.category" name="category" required>
          <option value="">Bitte wählen</option>
          <option value="Buch">Buch</option>
          <option value="Film">Film</option>
          <option value="PS-Spiel">PS-Spiel</option>
          <option value="Gesellschaftsspiel">Gesellschaftsspiel</option>
        </select>
      </div>

      
      <!-- Dynamische zusätzliche Felder -->
<div *ngFor="let field of customFields" class="form-group">
    <label>Eigenschaft:</label>
    <input type="text" [(ngModel)]="field.key" placeholder="z. B. Genre" [ngModelOptions]="{ standalone: true }" />
  
    <label>Wert:</label>
    <input type="text" [(ngModel)]="field.value" placeholder="z. B. Krimi" [ngModelOptions]="{ standalone: true }" />
  </div>

  <button type="button" (click)="addCustomField()">+ Feld hinzufügen</button>

  
      <div class="form-group">
        <label>Status:</label><br>
      
        <label>
          <input 
            type="radio" 
            name="status"
            [checked]="newMedium.status === 'offen'"
            (change)="toggleStatus('offen')" />
          Offen
        </label>
      
        <label>
          <input 
            type="radio" 
            name="status"
            [checked]="newMedium.status === 'erledigt'"
            (change)="toggleStatus('erledigt')" />
         erledigt
        </label>
      </div>
      
   
      <div *ngIf="newMedium.status === 'erledigt'">
        <div class="form-group">
          <label for="rating">Bewertung:</label>
          <input type="number" id="rating" name="rating" [(ngModel)]="newMedium.rating" min="1" max="5" />
        </div>
      
        <div class="form-group">
          <label for="comment">Kommentar:</label>
          <textarea id="comment" name="comment" [(ngModel)]="newMedium.comment"></textarea>
        </div>
      </div>
      

 
      <div class="modal-actions">
        <button type="submit">Speichern</button>
        <button type="button" (click)="closeModal()">Abbrechen</button>
      </div>
    </form>
  </div>
</div>
    </div>
</main>
