import { Component, OnInit } from '@angular/core';
import { MediaService } from '../shared/media.service';
import { CommonModule } from '@angular/common'; 

@Component({
  selector: 'app-home',
  standalone:true,
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  imports: [CommonModule]
})
export class HomeComponent implements OnInit {

  mediaList: any[] = [];

  constructor(private mediaService: MediaService) {}

  ngOnInit(): void {
    this.loadMedia();
  }

  // Lädt alle Medien vom Backend
  loadMedia(): void {
    this.mediaService.getAllMedia().subscribe({
      next: (data) => {
        this.mediaList = data;
      },
      error: (err) => {
        console.error('Fehler beim Laden der Medien:', err);
      }
    });
  }

  
  getKeys(obj: any): string[] {
    return Object.keys(obj);
  }

  categoryColors: { [key: string]: string } = {
    buch: '#f4a1a1',
    film: '#a6c5f1',
    'ps-spiel': '#a6f2a6',
    gesellschaftsspiel: '#e9b590'
  };
  
  getBackgroundColor(type: string): string {
    const key = type?.toLowerCase().replace(/\s+/g, '-');
    return this.categoryColors[key] || '#ffffff'; // Standardfarbe
  }
  
}
<main class="main-container">
  
    <div class="content-wrapper">
      
      <!-- Kategorien -->
      <ul class="category-list">
        <li class="category-item">Alle</li>
        <li class="category-item">Bücher</li>
        <li class="category-item">Filme</li>
        <li class="category-item">PS-Spiele</li>
        <li class="category-item">Gesellschaftsspiele</li>
      </ul>
      
  
      <!-- Hauptinhalt -->
      <section class="media-section">
       
  
        <div class="media-container">
          <h2>Deine Medien</h2>
        
      <div class="media-list">
        <div class="media-card" *ngFor="let medium of mediaList" 
     [ngStyle]="{ 'background-color': getBackgroundColor(medium.type) }">
          <h6 class="media-card-title">{{ medium.title }}</h6>
          <p class="media-card-type">{{ medium.type }}</p>

          <div class="media-card-details">
            <ng-container *ngFor="let key of getKeys(medium)">
                <p *ngIf="key !== 'title' && key !== 'type' && key !== '_id'">
                    <strong>{{ key | titlecase }}:</strong> {{ medium[key] }}
                </p>
            </ng-container>
          </div>
          
        </div>
      </div>
        </div>
      </section>
  
    </div>
  
  </main>
