import { Component, OnInit } from '@angular/core';
import { MediaService } from '../shared/media.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-home',
  standalone: true,
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  imports: [CommonModule, FormsModule]
})
export class HomeComponent implements OnInit {
  mediaList: any[] = [];
  showModal = false;
  newMedium: any = {
    title: '',
    category: '',
    status: false,
    rating: null,
    comment: ''
  };
  customFields: { key: string }[] = [];

  constructor(private mediaService: MediaService) {}

  ngOnInit(): void {
    this.loadMedia();
  }

  loadMedia(): void {
    this.mediaService.getAllMedia().subscribe({
      next: (data) => {
        this.mediaList = data;
      },
      error: (err) => {
        console.error('Fehler beim Laden der Medien:', err);
      }
    });
  }

  getKeys(obj: any): string[] {
    return Object.keys(obj);
  }

  getCategoryClass(category: string | undefined): string {
    if (!category || typeof category !== 'string') {
      return '';
    }
    return category.trim().toLowerCase().replace(/\s+/g, '-');
  }

  editMedium(medium: any): void {
    console.log("Bearbeiten:", medium);
  }

  deleteMedium(id: string): void {
    console.log("Löschen ID:", id);
  }

  openModal() {
    this.showModal = true;
  }

  closeModal() {
    this.showModal = false;
    this.resetNewMedium();
  }

  addCustomField() {
    this.customFields.push({ key: '' });
  }

  toggleStatus() {
    if (!this.newMedium.status) {
      this.newMedium.rating = null;
      this.newMedium.comment = '';
    }
  }

  onSubmit() {
    this.mediaService.createMedia(this.newMedium).subscribe({
      next: (medium) => {
        this.mediaList.push(medium);
        this.loadMedia();
        this.closeModal();
      },
      error: (err) => {
        console.error('Fehler beim Hinzufügen des Mediums:', err);
      }
    });
  }

  resetNewMedium() {
    this.newMedium = {
      title: '',
      category: '',
      status: false,
      rating: null,
      comment: ''
    };
    this.customFields = [];
  }
}
