import { Component, OnInit } from '@angular/core';
import { MediaService } from '../shared/media.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-home',
  standalone: true,
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  imports: [CommonModule, FormsModule]
})
export class HomeComponent implements OnInit {
  mediaList: any[] = [];
  showModal = false;

  newMedium: any = {
    title: '',
    category: '',
    status: false,
    rating: null,
    comment: ''
  };

  customFields: { key: string, value: string }[] = [];

  constructor(private mediaService: MediaService) {}

  ngOnInit(): void {
    this.loadMedia();
  }

  loadMedia(): void {
    this.mediaService.getAllMedia().subscribe({
      next: (data) => {
        this.mediaList = data;
      },
      error: (err) => {
        console.error('Fehler beim Laden der Medien:', err);
      }
    });
  }

  getKeys(obj: any): string[] {
    return Object.keys(obj);
  }

  getCategoryClass(category: string | undefined): string {
    return category?.trim().toLowerCase().replace(/\s+/g, '-') || '';
  }

  editMedium(medium: any): void {
    console.log("Medium bearbeiten:", medium);
  }

  deleteMedium(id: string): void {
    console.log("Medium löschen ID:", id);
  }

  openModal(): void {
    console.log("Modal öffnen");
    this.showModal = true;
  }

  closeModal(): void {
    this.showModal = false;
    this.resetForm();
  }

  addCustomField(): void {
    this.customFields.push({ key: '', value: '' });
  }

  toggleStatus(): void {
    if (!this.newMedium.status) {
      this.newMedium.rating = null;
      this.newMedium.comment = '';
    }
  }

  onSubmit(): void {
    const fullMedium = { ...this.newMedium };
    this.customFields.forEach(field => {
      if (field.key) {
        fullMedium[field.key] = field.value;
      }
    });

    this.mediaService.createMedia(fullMedium).subscribe({
      next: (medium) => {
        this.mediaList.push(medium);
        this.closeModal();
        this.loadMedia();
      },
      error: (err) => {
        console.error('Fehler beim Speichern:', err);
      }
    });
  }

  resetForm(): void {
    this.newMedium = {
      title: '',
      category: '',
      status: false,
      rating: null,
      comment: ''
    };
    this.customFields = [];
  }
}
