import { Component, OnInit } from '@angular/core';
import { MediaService } from '../shared/media.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms'; // Importiere FormsModule

interface Media {
  title: string;
  category: string;
  [key: string]: any;
}

@Component({
  selector: 'app-home',
  standalone: true,
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  imports: [CommonModule, FormsModule] // Füge FormsModule hier hinzu
})
export class HomeComponent implements OnInit {
  mediaList: Media[] = [];
  isNewMediumModalOpen = false;
  newMediumProperties: any[] = [
    { key: 'title', value: '', customKey: 'title' },
    { key: 'category', value: '', customKey: 'category' },
    { key: '', value: '', customKey: '' } // Die erste dynamische Eigenschaft
  ];
  propertySuggestions: string[] = ['Autor', 'Regisseur', 'Verlag', 'Spieleranzahl', 'Genre'];
  confirmationMessage: string = '';

  constructor(private mediaService: MediaService) {}

  ngOnInit(): void {
    this.loadMedia();
  }

  loadMedia(): void {
    this.mediaService.getAllMedia().subscribe({
      next: (data) => {
        this.mediaList = data;
      },
      error: (err) => {
        console.error('Fehler beim Laden der Medien:', err);
      }
    });
  }

  getKeys(obj: any): string[] {
    return Object.keys(obj);
  }

  getCategoryClass(category: string | undefined): string {
    if (!category || typeof category !== 'string') {
      return '';
    }
    return category.trim().toLowerCase().replace(/\s+/g, '-');
  }

  editMedium(medium: Media): void {
    console.log('Bearbeiten:', medium);
  }

  deleteMedium(id: string): void {
    console.log('Löschen ID:', id);
  }

  openNewMediumModal(): void {
    this.isNewMediumModalOpen = true;
  }

  closeModal(): void {
    this.isNewMediumModalOpen = false;
    this.newMediumProperties = [
      { key: 'title', value: '', customKey: 'title' },
      { key: 'category', value: '', customKey: 'category' },
      { key: '', value: '', customKey: '' }
    ];
    this.confirmationMessage = '';
  }

  addNewMediumProperty(): void {
    this.newMediumProperties.push({ key: '', value: '', customKey: '' });
  }

  addNewMedium(): void {
    const newMedium: Media = {
      title: this.newMediumProperties.find(p => p.key === 'title')?.value || '',
      category: this.newMediumProperties.find(p => p.key === 'category')?.value || ''
    };

    this.newMediumProperties.forEach(property => {
      if (property.key && property.key !== 'title' && property.key !== 'category' && property.value) {
        newMedium[property.key] = property.value;
      }
    });

    this.mediaService.createMedia(newMedium).subscribe({
      next: () => {
        this.confirmationMessage = 'Medium erfolgreich hinzugefügt!';
        this.loadMedia();
      },
      error: (err) => {
        console.error('Fehler beim Hinzufügen des Mediums:', err);
        this.confirmationMessage = 'Fehler beim Hinzufügen des Mediums.';
      }
    });
  }
}
