import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  standalone:true,
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  mediaList = [
    { title: 'Buchtitel', type: 'Buch', author: 'Max Mustermann', pages: 350, language: 'Deutsch', status: 'Gelesen' },
    { title: 'Filmname', type: 'Film', duration: 120, director: 'Steven Spielberg', status: 'Geschaut' },
    { title: 'Spielname', type: 'PS-Spiel', players: 4, platform: 'PlayStation 5', status: 'Gespielt' }
  ];

  constructor() { }

  ngOnInit(): void { }

  
}
<main class="main-container">
  
    <div class="content-wrapper">
      
      <!-- Kategorien -->
      <ul class="category-list">
        <li class="category-item">Alle</li>
        <li class="category-item">Bücher</li>
        <li class="category-item">Filme</li>
        <li class="category-item">PS-Spiele</li>
        <li class="category-item">Gesellschaftsspiele</li>
      </ul>
      
  
      <!-- Hauptinhalt -->
      <section class="media-section">
       
  
        <div class="media-container">
          <h2>Deine Medien</h2>
          <!-- Hier werden die Medien dynamisch eingefügt -->
        </div>
      </section>
  
    </div>
  
  </main>
