})
export class HomeComponent implements OnInit {
  mediaList = [
    { title: 'Buchtitel', type: 'Buch', author: 'Max Mustermann', pages: 350, language: 'Deutsch', status: 'Gelesen' },
    { title: 'Filmname', type: 'Film', duration: 120, director: 'Steven Spielberg', status: 'Geschaut' },
    { title: 'Spielname', type: 'PS-Spiel', players: 4, platform: 'PlayStation 5', status: 'Gespielt' }
  ];

  constructor() { }

  ngOnInit(): void { }

  getKeys(medium: any): string[] {
    return Object.keys(medium);
  }
}
