import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  standalone:true,
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  mediaList = [
    { title: 'Buchtitel', type: 'Buch', author: 'Max Mustermann', pages: 350, language: 'Deutsch', status: 'Gelesen' },
    { title: 'Filmname', type: 'Film', duration: 120, director: 'Steven Spielberg', status: 'Geschaut' },
    { title: 'Spielname', type: 'PS-Spiel', players: 4, platform: 'PlayStation 5', status: 'Gespielt' }
  ];

  constructor() { }

  ngOnInit(): void { }

  getKeys(medium: any): string[] {
    return Object.keys(medium);
  }
}
<main class="main-container">
  
    <div class="content-wrapper">
      
      <!-- Kategorien -->
      <aside class="sidebar">
        <h4>Kategorien</h4>
        <ul class="category-list">
          <li class="category-item"><a href="#">Alle</a></li>
          <li class="category-item"><a href="#">Bücher</a></li>
          <li class="category-item"><a href="#">Filme</a></li>
          <li class="category-item"><a href="#">PS-Spiele</a></li>
          <li class="category-item"><a href="#">Gesellschaftsspiele</a></li>
        </ul>
      </aside>
  
      <!-- Hauptinhalt -->
      <section class="media-section">
        <div class="action-buttons">
          <button class="btn btn-warning">Zufallsgenerator</button>
          <button class="btn btn-primary">Neues Medium hinzufügen</button>
        </div>
  
        <div class="media-container">
          <h2>Deine Medien</h2>
          <!-- Hier werden die Medien dynamisch eingefügt -->
        </div>
      </section>
  
    </div>
  
  </main>
